import React, { useRef } from "react";
import classes from "./Header.module.css";

import Button from "../UI/Button/Button";

const Header = (props) => {
  const isKpop = useRef(true);
  const isOpening = useRef(false);
  const isJpop= useRef(false);
  const isVideogame= useRef(false);

  const clickHandler = () =>{
    
  }

  return (
    <div className={classes.header} onClick = {clickHandler}>
      <div className={classes["search-header"]}>
        <Button
          ref = {isKpop}
          key="kpop"
          id="kpop"
          name="K-pop"
          className={classes["kpop-button"]}
        />
        <Button
          ref = {isOpening}
          key="openings"
          id="openings"
          name="Openings"
          className={classes["opening-button"]}
        />
        <Button
          ref={isJpop}
          key="jpop"
          id="jpop"
          name="J-pop"
          className={classes["jpop-button"]}
        />
        <Button
          ref = {isVideogame}
          key="videogames"
          id="kpop"
          name="Videogames"
          className={classes["videogames-button"]}
        />
      </div>
      <div className={classes["user-header"]}>
        <Button className={classes["help-button"]} />
        <Button className={classes["help-button"]} />
      </div>
    </div>
  );
};

export default Header;
