import React, { useState } from "react";
import classes from "./Button.module.css";

const Button = (props) => {
  const [buttonActive, setButtonActive] = useState(false);

  let buttonClasses = `${classes.button}, ${props.className}`;

  const growButtonHandler = (event) => {
    event.preventDefault();
    setButtonActive(true);
    console.log("I am activated");
    return (buttonClasses.join('grow-button'));
  };

  const decreaseButtonHandler = (event) => {
    event.preventDefault();
    setButtonActive(false);
    console.log("I am desactivated");
  };
  console.log("actual classes", buttonClasses);

  return (
    <React.Fragment>
      {!buttonActive && (
        <button
          type={props.type || "button"}
          className={buttonClasses}
          onClick={growButtonHandler}
        >
          {props.children}
        </button>
      )}

      {buttonActive && (
        <button
          type={props.type || "button"}
          className={buttonClasses}
          onClick={decreaseButtonHandler}
        >
          {props.children}
        </button>
      )}
    </React.Fragment>
  );
};

export default Button;
