import { useState } from "react/cjs/react.production.min";
import Header from "./components/Layout/Header";
import MusicList from "./components/Music/MusicList";

function App() {
  const [music, setMusic] = useState([]);
  /*
  const musicList = [
    {
      id: "1",
      title: "unravel",
      album: "album_1",
      author: "author_1",
    },
    {
      id: "2",
      title: "title_2",
      album: "album_2",
      author: "author_2",
    },
  ];
  */

  async function fetchHandler() {
    const response = await fetch('https://swapi.dev/api/films/');
    const data = await response.json();
    const transformedMusic = data.results.map((musicData) => {
      return {
        id: musicData.episode_id,
        title: musicData.title,
        album: musicData.opening_crawl,
        author: musicData.release_date,
      };
    });
    setMusic(transformedMusic);
  };

  return (
    <div>
      <section>
        <Header />
      </section>
      <section>
        <button onClick={fetchHandler}></button>
        <MusicList music={music} />
      </section>
    </div>
  );
}

export default App;
