import React from 'react';
import { useState } from 'react/cjs/react.production.min';
import Header from "./components/Layout/Header";
import MusicList from "./components/Music/MusicList";

function App() {
  const [isKmusic, setIsKMusic] = useState(true);
  const [isJmusic, setIsJMusic] = useState(false);
  //DUMMY MUSIC
    const kMusicList = [
      {
        id: "1",
        title: "KMusic",
        album: "album_1",
        author: "author_1",
      },
      {
        id: "2",
        title: "KMusic",
        album: "album_2",
        author: "author_2",
      },
    ];

    const jMusicList = [
      {
        id: "1",
        title: "JMusic",
        album: "album_1",
        author: "author_1",
      },
      {
        id: "2",
        title: "JMusic",
        album: "album_2",
        author: "author_2",
      },
    ];

    let content;
    if(isKmusic){
      content : <MusicList music={kMusicList} />
    }
    if(isJmusic){
      content: <MusicList music={jMusicList} />;
    }
  return (
    <div>
      <section>
        <Header/>
      </section>
      <section>
        {content}
      </section>
    </div>
  );
}

export default App;
