import { useCallback, useState } from "react/cjs/react.production.min";
import Header from "./components/Layout/Header";
import MusicList from "./components/Music/MusicList";

function App() {
  const [music, setMusic]= useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const musicList = [
    {
      id: "1",
      title: "unravel",
      album: "album_1",
      author: "author_1",
    },
    {
      id: "2",
      title: "title_2",
      album: "album_2",
      author: "author_2",
    },
  ];
    const fetchHandler = useCallback(async()=>{
      setIsLoading(true);

      try{
        const response = await fetch("https://theaudiodb.p.rapidapi.com/playlist.php?s=Top_Popular_Tracks_2019", {
          "method": "GET",
          "headers": {
            "x-rapidapi-key": "afc7c5ac22mshea40c7eb8bdd14fp1c7715jsn48105b87b3fe",
            "x-rapidapi-host": "theaudiodb.p.rapidapi.com"
          }
        });
        if (!response.ok) {
          throw new Error('Something went wrong!');
        }
        const data = await response.json();
        const transformedMusic = data.results.map((musicData) => {
          return {
            id: musicData.pos,
            title: musicData.name,
            album: musicData.name,
            author: musicData.urlvideo,
          };
        });
        setMusic(transformedMusic);

      }catch(err){
         setError(error.message);
      }
      setIsLoading(false);
    }, []);

    useEffect(() => {
      fetchHandler();
    }, [fetchHandler]);

    let content = <p>Found no music.</p>;

    if (music.length > 0) {
      content = <MusicList movies={movies} />;
    }

    if (error) {
      content = <p>{error}</p>;
    }

    if (isLoading) {
      content = <p>Loading...</p>;
    }
  return (
    <div>
      <section>
        <Header />
      </section>
      <section>
        <MusicList music={music} />
      </section>
    </div>
  );
}

export default App;
