import React from "react";
import { useState } from "react/cjs/react.production.min";
import Header from "./components/Layout/Header";
import MusicList from "./components/Music/MusicList";

function App() {
  const [isKmusic, setIsKMusic] = useState(true);
  //DUMMY MUSIC
  const kMusicList = [
    {
      id: "1",
      title: "unravel",
      album: "album_1",
      author: "author_1",
    },
    {
      id: "2",
      title: "title_2",
      album: "album_2",
      author: "author_2",
    },
  ];

  const jMusicList = [
    {
      id: "1",
      title: "JMusic",
      album: "album_1",
      author: "author_1",
    },
    {
      id: "2",
      title: "title_2",
      album: "album_2",
      author: "author_2",
    },
  ];

  const changeGenreHandler = () => {
    if (isKmusic === true) {
      setIsKMusic(true);
    }
    setIsKMusic(false);
  };

  return (
    <div>
      <section>
        <Header />
      </section>
      <section>
        <button onClick={changeGenreHandler}></button>
      </section>
      <section>
        {isKmusic && <MusicList music={kMusicList} />}
        {!isKmusic && <MusicList music={jMusicList} />}
      </section>
    </div>
  );
}

export default App;
