import React, { useRef } from "react";
import classes from "./Header.module.css";

import Button from "../UI/Button/Button";

const Header = (props) => {
  const kpopRef = useRef();
 
  const clickHandler = () => {
    const wrapper = kpopRef.current;
    wrapper.classList.toogle('is_open')
  };

  return (
    <div className={classes.header}>
      <div className={classes["search-header"]}>
        <Button onClick = {clickHandler} ref = {kpopRef}/>
        <Button></Button>
        <Button></Button>
        <Button></Button>
      </div>
      <div className={classes["user-header"]}>
        <Button></Button>
        <Button></Button>
      </div>
    </div>
  );
};

export default Header;
