import React,{useRef} from "react";
import classes from "./Button.module.css";

const Button = (props) => {
  const buttonRef = useRef();

  const clickHandler = () => {
    const wrapper = kpopRef.current;
    wrapper.classList.toogle(`${classes.button} ${classes['is_open']} ${props.className}`);
  };
  return (
    <button
      ref={buttonRef}
      onClick={clickHandler}
      type={props.type || "button"}
      className={`${classes.button} ${classes['is_closed']} ${props.className}`}
    >
      {props.children}
    </button>
  );
};

export default Button;
