import classes, { useState } from "./Card.module.css";

const Card = (props) => {
  const [cardClicked, setCardClicked] = useState(false);

  let cardClasses = `${classes.card}  ${
    cardClicked ? classes["card-opened"] : ""
  }`;

  const openCardHandler = (event) => {
    event.preventDefault();
    if (cardClicked == true) {
      setCardClicked(false);
      console.log("I am desactivated");
      return;
    }
    setCardClicked(true);
    console.log("I am activated");
  };
  return (
    <React.Fragment>
      <div onClick={openCardHandler} className={cardClasses}>
        {props.children}
      </div>
    </React.Fragment>
  );
};

export default Card;
